import React, { useEffect } from 'react'
import { useState } from 'react'
import axios from 'axios'

export default function Home() {
  const [data, setData] = useState([]);
  // useEffect(() => {
  //   axios.get(' https://newsapi.org/v2/top-headlines?country=in&apiKey=c74970e40cf34fdba2a15eb9c954fa69')
  //     .then((res) => {
  //       setData(res.data.articles);
  //     });
  // })

  const categoryChangeToG =() =>{
    axios.get('https://newsapi.org/v2/top-headlines?country=in&category=general&apiKey=c74970e40cf34fdba2a15eb9c954fa69')
      .then((res) => {
        setData(res.data.articles);
      });
  }
  const categoryChange =() =>{
    axios.get('https://newsapi.org/v2/top-headlines?country=in&category=sports&apiKey=c74970e40cf34fdba2a15eb9c954fa69')
      .then((res) => {
        setData(res.data.articles);
      });
  }

  const news = data.map((value, index) => {
    return (
      <div key={index} className="card">
        <img src={value.urlToImage} alt="..." />
        <div className='card-body'>
          <h4 className='title'>{value.title}</h4>
          <p className='description'>{value.description}</p>
          <a href={value.url} className="btn">READ MORE</a>
        </div>
      </div>
    )
  })
  return (
    <div>

      <div className='mainbox'>
        <div className='sidebox'>
          <div className='category'> 
          <h2>Category</h2>
            <button onClick={categoryChangeToG}>General</button>
            <br />
            <button onClick={categoryChange}>Sports</button>
            <br />
            <button onClick={categoryChange}>Science</button>
            <br />
            <button onClick={categoryChange}>Entertainment</button>
            <br />
            <button onClick={categoryChange}>Technology</button>
            <br />
            <button onClick={categoryChange}>Business</button>
          </div>
          <div>
            <h2>Country</h2>
            <button onClick={categoryChange}>India</button><br />
            <button onClick={categoryChange}>USA</button><br />
            <button onClick={categoryChange}>Canada</button>
          </div>
        </div>
        <div className='centerbox'>
{news}
        </div>
      </div>


      {/* // display flex row
      // card map state and props
      // sidebar == button with on click
      // CATEGORY == general sports science entertainment technology business health
      // COUNTRY === india USA Canada  */}
    </div>

  )


}
