import React, { useEffect } from 'react'
import { useState } from 'react'
import axios from 'axios'

export default function Home() {
  const [data, setData] = useState([]);
  useEffect(() => {
    axios.get(' https://newsapi.org/v2/top-headlines?country=us&apiKey=c74970e40cf34fdba2a15eb9c954fa69')
      .then((res) => {
        setData(res.data.articles);
      });
  })

  const news = data.map((value, index) => {
    return (
      <div key={index} className="card">
        <img src={value.urlToImage} alt="..." />
        <div className='card-body'>
          <h4 className='title'>{value.title}</h4>
          <p className='description'>{value.description}</p>
          <a href={value.url} className="btn">READ MORE</a>
        </div>
      </div>
    )
  })
  return (
    <div>

      <div className='mainbox'>
        <div className='sidebox'>
          <div className='category'> Category
            <button>General</button>
            <br />
            <button>Sports</button>
            <br />
            <button>Science</button>
            <br />
            <button>Entertainment</button>
            <br />
            <button>Technology</button>
            <br />
            <button>Business</button>
          </div>
          <div>Country
            <button>India</button>
            <button>USA</button>
            <button>Canada</button>
          </div>
        </div>
        <div className='centerbox'>
{news}
        </div>
      </div>


      {/* // display flex row
      // card map state and props
      // sidebar == button with on click
      // CATEGORY == general sports science entertainment technology business health
      // COUNTRY === india USA Canada  */}
    </div>

  )


}
